sql.where:
1 = 1

    AND id = #{id}


    AND agentUserId = #{agentuserid}


    AND merchantId = #{merchantid}


    AND productInfoId = #{productinfoid}


    AND agentTime = #{agenttime}


    AND updateTime = #{updatetime}


