mapper.update:
UPDATE agent_merchant
agentUserId = #{agentuserid}, merchantId = #{merchantid}, productInfoId = #{productinfoid}, agentTime = #{agenttime}, updateTime = #{updatetime}, status = #{status},
where id = #{id}

UPDATE agent_merchant
agentTime = #{agenttime}, updateTime = #{updatetime}, status = #{status},
where id = #{id} and agentUserId = #{agentUserId}
