sql.where:
1=1
AND so.id = #{id}
AND so.orderNum = #{orderNum}
AND so.buyUserId = #{buyUserId}
AND so.shareUserId = #{shareUserId}
AND so.shareUsername = #{shareUsername}
AND so.buyUsername = #{buyUsername}
AND so.shareMechanism = #{shareMechanism}
AND so.commission = #{commission}
AND so.IsCommission = #{IsCommission}
AND so.merchantName = #{merchantName}
AND so.orderMoney = #{orderMoney}

    and so.createTime >= #{startTime}


    and so.createTime <= #{endTime}


    and so.createTime BETWEEN #{startTime} AND #{endTime}

AND so.updateTime = #{updateTime}
AND so.payStatus = #{payStatus}
AND so.status = #{status}

mapper.select:
select
(
select SUM(so.orderMoney)
FROM share_order so
LEFT JOIN orders o ON so.orderNum = o.orderNum
where so.shareUserId = #{userId}
) total ,
(
select SUM(so.orderMoney)
FROM share_order so
LEFT JOIN orders o ON so.orderNum = o.orderNum
where so.shareUserId = #{userId}
AND o.finishTime > '2018-07-04 08:36:04'
) waitRevenue ,
(
select COUNT(*)
FROM share_order so
LEFT JOIN orders o ON so.orderNum = o.orderNum
where so.shareUserId = #{userId}
AND o.finishTime > #{finishTime}
) completeOrder ,
(
select COUNT(*)
FROM share_order so
LEFT JOIN orders o ON so.orderNum = o.orderNum
where so.shareUserId = #{userId}
AND o.finishTime < #{finishTime}
) orderInTransaction
